import React from 'react';
import * as THREE from 'three'
import { Suspense, useRef } from 'react'
import { Canvas, useFrame, useThree } from '@react-three/fiber'
import { ScrollControls, Scroll, useScroll, Preload, Image as ImageImpl, Text, Html } from '@react-three/drei'
import Home from "./Home";

function Image(props) {
  const ref = useRef()
  const group = useRef()
  const data = useScroll()
  const w = 1
  const gap = 0.25
  const xW = w + gap
  const img_w = height * 0.9 * 1.75;
  const imgs_scr_w = img_w + xW * 2;
  const { width, height } = useThree((state) => state.viewport)
  useFrame((state, delta) => {
    // console.log(data)
    // const a = data.range(0, 1 / 3) + data.range(1 / 3, 1 / 3) + data.range(2 / 3, 1 / 3)
    // const b = data.range(1 / 3, 1 / 3)
    // const c = data.range(1/3, 1/3)
    const effect_index = Math.round(data.range(0, 1 / 3) + data.range(1 / 3, 1 / 3) + data.range(2 / 3, 1 / 3))
    switch (effect_index) {
      case 0:
        // ref.current.position.x = THREE.MathUtils.damp(txtRef.current.position.x, 0, 6, delta)
        // img1Ref.current.position.x = THREE.MathUtils.damp(img1Ref.current.position.x, width * 0.35, 6, delta)
        // img2Ref.current.position.x = THREE.MathUtils.damp(img2Ref.current.position.x, width * 0.35 + height * 0.9 * 1.75 / 2 + 0.75, 6, delta)
        // img3Ref.current.position.x = THREE.MathUtils.damp(img3Ref.current.position.x, width * 0.35 + height * 0.9 * 1.75 / 2 + 0.75 + 1.25, 6, delta)

        break;
      case 1:
        ref.current.position.x = THREE.MathUtils.damp(ref.current.position.x, 0, 6, delta)

        break;
      case 2:

        break;
      case 3:

        break;
      default:
        break;
    }
    // console.log("eeeeeeee", c);
    // console.log(Math.round(a))
    // group.current.position.z = THREE.MathUtils.damp(group.current.position.z, Math.max(0, data.delta * 50), 4, delta)
    // ref.current.material.grayscale = THREE.MathUtils.damp(ref.current.material.grayscale, Math.max(0, 1 - data.delta * 1000), 4, delta)
  })
  return (
    <group ref={group}>
      <ImageImpl ref={ref} {...props} />
    </group>
  )
}

function Hometxt(props) {

  useFrame((state, delta) => {

  })
  return (
    <div id='home_txt'>
      <p id='home_title'>Photo-realistic 3d renders</p>
      <p id='home_subtitle'>Renders are digital representations of projects recreated in 3D. They are the ideal tool to effectively present a property under construction or renovation as it allows you to see the final result without any need to wait for the work to be completed. They find use in revolutionizing architecture, construction, interior design and recently, real estate as well.</p>
      <img id='home_img' src='/img/btn.jpg' />
    </div>
  )
}

function Page({ m = 0.4, urls, ...props }) {
  const { width } = useThree((state) => state.viewport)
  const w = width < 10 ? 1.5 / 3 : 1 / 3
  return (
    <group {...props}>
      <Image position={[-width * w, 0, -1]} scale={[width * w - m * 2, 5, 1]} url={urls[0]} />
      <Image position={[0, 0, 0]} scale={[width * w - m * 2, 5, 1]} url={urls[1]} />
      <Image position={[width * w, 0, 1]} scale={[width * w - m * 2, 5, 1]} url={urls[2]} />
    </group>
  )
}

function Pages() {
  const { width } = useThree((state) => state.viewport)
  return (
    <>
      {/* <Page position={[-width * 1, 0, 0]} urls={['/trip1.jpg', '/trip2.jpg', '/trip3.jpg']} /> */}
      <Page position={[width * 0, 0, 0]} urls={['/img1.jpg', '/img2.jpg', '/img3.jpg']} />
      {/* <Page position={[width * 1, 0, 0]} urls={['/img4.jpg', '/img5.jpg', '/img6.jpg']} /> */}
      {/* <Page position={[width * 2, 0, 0]} urls={['/trip1.jpg', '/trip2.jpg', '/trip3.jpg']} /> */}
      {/* <Page position={[width * 3, 0, 0]} urls={['/img1.jpg', '/img2.jpg', '/img3.jpg']} /> */}
      {/* <Page position={[width * 4, 0, 0]} urls={['/img4.jpg', '/img5.jpg', '/img6.jpg']} /> */}
    </>
  )
}

function Items({ w = 1, gap = 0.25 }) {
  const { width, height } = useThree((state) => state.viewport)
  const xW = w + gap
  const img_w = height * 0.9 * 1.75;
  const imgs_scr_w = img_w + xW * 2;
  const total_scr_w = img_w / 2 - (width / 2 - width * 0.35) + xW * 2 + (width - imgs_scr_w) / 2 + width
  return (
    <ScrollControls horizontal damping={4} pages={total_scr_w / width} distance={1}>
          <Scroll html>
            {/* <h1 style={{ position: 'absolute', top: '20vh', left: '-75vw' }}>home</h1> */}
            {/* <h1 id='teseid' style={{ position: 'absolute', top: '20vh', left: '25vw' }}>to</h1> */}
            {/* <p id='home_title'>Photo-realistic 3d renders</p>
            <p id='home_subtitle'>Renders are digital representations of projects recreated in 3D. They are the ideal tool to effectively present a property under construction or renovation as it allows you to see the final result without any need to wait for the work to be completed. They find use in revolutionizing architecture, construction, interior design and recently, real estate as well.</p>
            <img id='home_img' src='/img/btn.jpg' /> */}
        {/* <div id='home_txt'>
          <p id='home_title'>Photo-realistic 3d renders</p>
          <p id='home_subtitle'>Renders are digital representations of projects recreated in 3D. They are the ideal tool to effectively present a property under construction or renovation as it allows you to see the final result without any need to wait for the work to be completed. They find use in revolutionizing architecture, construction, interior design and recently, real estate as well.</p>
          <img id='home_img' src='/img/btn.jpg' />
        </div> */}

            {/* <h1 style={{ position: 'absolute', top: '20vh', left: '125vw' }}>be</h1> */}
            {/* <h1 style={{ position: 'absolute', top: '20vh', left: '225vw' }}>home</h1> */}
            {/* <h1 style={{ position: 'absolute', top: '20vh', left: '325vw' }}>to</h1> */}
            {/* <h1 style={{ position: 'absolute', top: '20vh', left: '425vw' }}>be</h1> */}
          </Scroll>
          <Scroll>
            {/* <Pages /> */}

          </Scroll>
          <Backs/>


        </ScrollControls>
  )
}

function Backs() {
  const txtRef = useRef()
  const { width, height } = useThree((state) => state.viewport)
  const data = useScroll()
  useFrame((state, delta) => {
    // txtRef.current.style.left = -width * 0.1 + "px"
    console.log("aaasssss", txtRef.current.position)
    const effect_index = Math.round(data.range(0, 1 / 3) + data.range(1 / 3, 1 / 3) + data.range(2 / 3, 1 / 3))
    switch (effect_index) {
      case 0:
        // txtRef.current.position.x = THREE.MathUtils.damp(txtRef.current.position.x, 0, 6, delta)
        // img1Ref.current.position.x = THREE.MathUtils.damp(img1Ref.current.position.x, width * 0.35, 6, delta)
        // img2Ref.current.position.x = THREE.MathUtils.damp(img2Ref.current.position.x, width * 0.35 + height * 0.9 * 1.75 / 2 + 0.75, 6, delta)
        // img3Ref.current.position.x = THREE.MathUtils.damp(img3Ref.current.position.x, width * 0.35 + height * 0.9 * 1.75 / 2 + 0.75 + 1.25, 6, delta)

        break;
      case 1:

        break;
      case 2:

        break;
      case 3:

        break;
      default:
        break;
    }
    // const b = data.range(1 / 3, 1 / 3)
    // const c = data.range(2 / 3, 1 / 3)
    // console.log("eeeeeeee");
    // console.log(Math.round(a))
    // group.current.position.z = THREE.MathUtils.damp(group.current.position.z, Math.max(0, data.delta * 50), 4, delta)
    // ref.current.material.grayscale = THREE.MathUtils.damp(ref.current.material.grayscale, Math.max(0, 1 - data.delta * 1000), 4, delta)
  })
  return (
    <group>
      <Html ref={txtRef} fullscreen zIndexRange={[-100, -2]} position={[0,0,0]}>
        <div id='home_txt'>
          <p id='home_title'>Photo-realistic 3d renders</p>
          <p id='home_subtitle'>Renders are digital representations of projects recreated in 3D. They are the ideal tool to effectively present a property under construction or renovation as it allows you to see the final result without any need to wait for the work to be completed. They find use in revolutionizing architecture, construction, interior design and recently, real estate as well.</p>
          <img id='home_img' src='/img/btn.jpg' />
        </div>
      </Html>
      <Image position={[0, 0, 0]} scale={[height * 0.9 * 1.75 , height * 0.9, 1]} url={"/img1.jpg"} />
      {/* <Image position={[width * 0.35 + height * 0.9 * 1.75, 0, 0]} scale={[height * 0.9 * 1.75, height * 0.9, 1]} url={'/img2.jpg'} />  */}
      <Image position={[width * 0.35 + height * 0.9 * 1.75 / 2 + 0.75, 0, 0]} scale={[1, height * 0.9, 1]} url={'/img2.jpg'} /> 
      {/* <Image position={[width * 0.8, 0, 0]} scale={[height * 0.9 * 1.75, height * 0.9, 1]} url={'/img3.jpg'} /> */}
      <Image position={[width * 0.35 + height * 0.9 * 1.75 / 2 + 0.75 + 1.25, 0, 0]} scale={[1, height * 0.9, 1]} url={'/img3.jpg'} />
    </group>
  )
}

export default function App() {
  return (
    <Canvas gl={{ antialias: false }} dpr={[1, 1]}>
      <Suspense fallback={null}>
        <Items/>
        <Preload />
      </Suspense>
    </Canvas>
  )
}
